import os# os.system('python3 --version') # 执行终端指令print(os.getcwd()) # 返回当前的工作路径now_path = os.getcwd()print(os.path.isabs(now_path)) # 判断是否是绝对路径print(os.path.isfile(now_path)) # 判断是否是一个文件夹print(os.path.isdir(now_path)) # 验证给出是否是一个目录print(os.sep) # 文件路径的分隔符windows上面的是'\' Linux上面的是'/'walk = os.walk('/Users/sandy/Desktop/Python3/tensorflow练习')for root, dirs, files in os.walk('/Users/sandy/Desktop/Python3/tensorflow练习'):    print('root_dir', root) # 当前目录路径    print("sub_dirs:", dirs)# 当前目录下面的所有子路径    print("files:", files) # 当前路径下面的所有非子路径print(os.linesep)print(os.name) # 指示你正在使用的平台：	如:对于Windows，它是'nt'，而对于Linux/Unix用户，它是'posix'# print(os.chmod(now_path)) # 修改文件权限与时间戳os.exit()print(os.stat(now_path))